<?php get_header(); ?><div id="primary-content">		<?php if(have_posts()): while(have_posts()): the_post(); ?>			<div class="bg-post">				<?php 					if(has_post_thumbnail()) {?>				<?php the_post_thumbnail(); ?>				<?php } else {?>				<?php					  }				?>			</div>			<div class="entry">				<div class="pr-content">					<?php the_content(); ?>				</div>			</div>		<?php endwhile ; ?>		<?php else : ?>			<div class="entry">				<p>Updating...</p>			</div>		<?php endif; ?>		<div style="clear: both;"></div>			<div class="page-navigation">			<?php				global $wp_query;				$big = 999999999; // need an unlikely integer				echo paginate_links( array(					'base' => str_replace( $big, '%#%', esc_url( get_pagenum_link( $big ) ) ),					'format' => '?paged=%#%',					'current' => max( 1, get_query_var('paged') ),					'total' => $wp_query->max_num_pages				));				?>		</div>	</div>		
<?php get_footer(); ?>