﻿<?php get_header(); ?><div id="primary-content">  <h2 class="h-title"><?php wp_title('');?></h2>						<h3 class="sub-h-title"><?php echo category_description(); ?></h3>   	<div class="bg-post">		<img width="2667" height="1403" src="http://new.hunters.co.jp/wp-content/uploads/2016/03/news-bg.jpg" class="attachment-full size-full wp-post-image" alt="about-us-bg" />							  </div>  <div class="entry">  <div class="pr-content">  <ul id="arc-pos">  <?php wp_get_archives( array( 'type' => 'yearly', 'limit' => 12 ) ); ?>  </ul>  <ul class="home-pr">		<?php if(have_posts()): while(have_posts()): the_post(); ?>		<li>			<span class="date"><span><?php the_time('d'); ?></span><span><?php the_time('M'); ?></span></span>			<div class="list-ds">			<h3><a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a></h3>			<div class="cat-descp">					<?php the_excerpt(); ?>			</div>			</div>		</li>		<?php endwhile ; ?>		<?php else : ?>		<?php endif; ?>		</ul>		<div style="clear: both;"></div>			<div class="page-navigation">			<?php				global $wp_query;				$big = 999999999; // need an unlikely integer				echo paginate_links( array(					'base' => str_replace( $big, '%#%', esc_url( get_pagenum_link( $big ) ) ),					'format' => '?paged=%#%',					'current' => max( 1, get_query_var('paged') ),					'total' => $wp_query->max_num_pages				));				?>		</div>	</div>	</div>	</div>		<?php get_footer(); ?>